package repp

import (
	"reflect"
	"testing"

	"github.com/go-test/deep"
)

func Test_recogRegex(t *testing.T) {
	type args struct {
		recog string
	}
	tests := []struct {
		name        string
		args        args
		wantDecoded string
	}{
		{
			"decode PpuMI: RGGWCCY",
			args{
				recog: "RGGWCCY",
			},
			"(A|G)GG(A|T)CC(C|T)",
		},
	}
	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			if gotDecoded := recogRegex(tt.args.recog); gotDecoded != tt.wantDecoded {
				t.Errorf("recogRegex() = %v, want %v", gotDecoded, tt.wantDecoded)
			}
		})
	}

	// should be able to decode every recognition site without failing
	for _, enz := range NewEnzymeDB().contents {
		recogRegex(newEnzyme("", enz).recog)
	}
}

func Test_digest(t *testing.T) {
	type args struct {
		frag *Frag
		enz  []enzyme
	}
	tests := []struct {
		name         string
		args         args
		wantDigested *Frag
		wantBackbone *Backbone
		wantErr      bool
	}{
		{
			"fail with no recognition sequence",
			args{
				&Frag{
					Seq: "ATGAGGTTAGCCAAAAAAGCACGTGGTGGCGCCCACCGACTGTTCCCAAACTGTAGCTCTTCGTTCCGTCAAGGCCCGACTTTCATCGCGGCCCATTCCA",
				},
				[]enzyme{
					{recog: "GAATTC", compCutIndex: 5, seqCutIndex: 1},
				},
			},
			&Frag{},
			&Backbone{},
			true,
		},
		{
			"digest in sequence no overhang",
			args{
				&Frag{
					Seq: "ATGAGGTTAGCCAAAAAAGCACGTGAATTCGGTGGCGCCCACCGACTGTTCCCAAACTGTAGCTCTTCGTTCCGTCAAGGCCCGACTTTCATCGCGGCCCATTCCA",
				},
				[]enzyme{
					{name: "E1", recog: "GAATTC", seqCutIndex: 3, compCutIndex: 3},
				},
			},
			&Frag{
				uniqueID: "backbone",
				fragType: linear,
				Seq:      "TTCGGTGGCGCCCACCGACTGTTCCCAAACTGTAGCTCTTCGTTCCGTCAAGGCCCGACTTTCATCGCGGCCCATTCCAATGAGGTTAGCCAAAAAAGCACGTGAA",
			},
			&Backbone{
				Seq:      "ATGAGGTTAGCCAAAAAAGCACGTGAATTCGGTGGCGCCCACCGACTGTTCCCAAACTGTAGCTCTTCGTTCCGTCAAGGCCCGACTTTCATCGCGGCCCATTCCA",
				Enzymes:  []string{"E1"},
				Cutsites: []int{27},
				Strands:  []bool{true},
			},
			false,
		},
		{
			"digest in reverse complement sequence no overhang",
			args{
				&Frag{
					Seq: "ATGAGGTTAGCCAAAAAAGCACGCTGGGGGTGGCGCCCACCGACTGTTCCCAAACTGTAGCTCTTCGTTCCGTCAAGGCCCGACTTTCATCGCGGCCCATTCCA",
				},
				[]enzyme{{name: "E2", recog: "CCCAGC", seqCutIndex: 3, compCutIndex: 3}}, // rev comp GCTGGG
			},
			&Frag{
				uniqueID: "backbone",
				fragType: linear,
				Seq:      "GGGGGTGGCGCCCACCGACTGTTCCCAAACTGTAGCTCTTCGTTCCGTCAAGGCCCGACTTTCATCGCGGCCCATTCCAATGAGGTTAGCCAAAAAAGCACGCT",
			},
			&Backbone{
				Seq:      "ATGAGGTTAGCCAAAAAAGCACGCTGGGGGTGGCGCCCACCGACTGTTCCCAAACTGTAGCTCTTCGTTCCGTCAAGGCCCGACTTTCATCGCGGCCCATTCCA",
				Cutsites: []int{25},
				Strands:  []bool{false},
				Enzymes:  []string{"E2"},
			},
			false,
		},
		{
			"digest in sequence positive overhang",
			args{
				&Frag{
					Seq: "ATGAGGTTAGCCAAAAAAGCACGTGAATTCGGTGGCGCCCACCGACTGTTCCCAAACTGTAGCTCTTCGTTCCGTCAAGGCCCGACTTTCATCGCGGCCCATTCCA",
				},
				[]enzyme{
					{name: "E3", recog: "GAATTC", seqCutIndex: 1, compCutIndex: 5},
				},
			},
			&Frag{
				uniqueID: "backbone",
				fragType: linear,
				Seq:      "AATTCGGTGGCGCCCACCGACTGTTCCCAAACTGTAGCTCTTCGTTCCGTCAAGGCCCGACTTTCATCGCGGCCCATTCCAATGAGGTTAGCCAAAAAAGCACGTG",
			},
			&Backbone{
				Seq:      "ATGAGGTTAGCCAAAAAAGCACGTGAATTCGGTGGCGCCCACCGACTGTTCCCAAACTGTAGCTCTTCGTTCCGTCAAGGCCCGACTTTCATCGCGGCCCATTCCA",
				Cutsites: []int{25},
				Strands:  []bool{true},
				Enzymes:  []string{"E3"},
			},
			false,
		},
		{
			"digest in sequence, negative overhang",
			args{
				&Frag{
					Seq: "ATGAGGTTAGCCAAAAAAGCACGTGAATTCGGTGGCGCCCACCGACTGTTCCCAAACTGTAGCTCTTCGTTCCGTCAAGGCCCGACTTTCATCGCGGCCCATTCCA",
				},
				[]enzyme{
					{name: "E4", recog: "GAATTC", seqCutIndex: 5, compCutIndex: 1},
				},
			},
			&Frag{
				uniqueID: "backbone",
				fragType: linear,
				Seq:      "CGGTGGCGCCCACCGACTGTTCCCAAACTGTAGCTCTTCGTTCCGTCAAGGCCCGACTTTCATCGCGGCCCATTCCAATGAGGTTAGCCAAAAAAGCACGTGAATT",
			},
			&Backbone{
				Seq:      "ATGAGGTTAGCCAAAAAAGCACGTGAATTCGGTGGCGCCCACCGACTGTTCCCAAACTGTAGCTCTTCGTTCCGTCAAGGCCCGACTTTCATCGCGGCCCATTCCA",
				Cutsites: []int{29},
				Strands:  []bool{true},
				Enzymes:  []string{"E4"},
			},
			false,
		},
		{
			"digest in reverse complement sequence, positive overhang",
			args{
				&Frag{
					Seq: "ATGAGGTTAGCCAAAAAAGCACGTGCTGGGGGTGGCGCCCACCGACTGTTCCCAAACTGTAGCTCTTCGTTCCGTCAAGGCCCGACTTTCATCGCGGCCCATTCCA",
				},
				[]enzyme{
					{name: "E5", recog: "CCCAGC", seqCutIndex: 1, compCutIndex: 5},
				}, // rev comp = GCTGGG
			},
			&Frag{
				uniqueID: "backbone",
				fragType: linear,
				Seq:      "CTGGGGGTGGCGCCCACCGACTGTTCCCAAACTGTAGCTCTTCGTTCCGTCAAGGCCCGACTTTCATCGCGGCCCATTCCAATGAGGTTAGCCAAAAAAGCACGTG",
			},
			&Backbone{
				Seq:      "ATGAGGTTAGCCAAAAAAGCACGTGCTGGGGGTGGCGCCCACCGACTGTTCCCAAACTGTAGCTCTTCGTTCCGTCAAGGCCCGACTTTCATCGCGGCCCATTCCA",
				Cutsites: []int{25},
				Strands:  []bool{false},
				Enzymes:  []string{"E5"},
			},
			false,
		},
		{
			"digest in reverse complement sequence, negative overhang",
			args{
				&Frag{
					Seq: "ATGAGGTTAGCCAAAAAAGCACGTGCTGGGGGTGGCGCCCACCGACTGTTCCCAAACTGTAGCTCTTCGTTCCGTCAAGGCCCGACTTTCATCGCGGCCCATTCCA",
				},
				[]enzyme{
					{name: "E6", recog: "CCCAGC", seqCutIndex: 5, compCutIndex: 1},
				}, // rev comp = GCTGGG
			},
			&Frag{
				uniqueID: "backbone",
				fragType: linear,
				Seq:      "GGGTGGCGCCCACCGACTGTTCCCAAACTGTAGCTCTTCGTTCCGTCAAGGCCCGACTTTCATCGCGGCCCATTCCAATGAGGTTAGCCAAAAAAGCACGTGCTGG",
			},
			&Backbone{
				Seq:      "ATGAGGTTAGCCAAAAAAGCACGTGCTGGGGGTGGCGCCCACCGACTGTTCCCAAACTGTAGCTCTTCGTTCCGTCAAGGCCCGACTTTCATCGCGGCCCATTCCA",
				Cutsites: []int{29},
				Strands:  []bool{false},
				Enzymes:  []string{"E6"},
			},
			false,
		},
		{
			"digest circular pSB1A3 backbone with PstI",
			args{
				&Frag{
					// the sequence is already doubled
					Seq: "",
				},
				[]enzyme{
					{name: "PstI", recog: "CTGCAG", seqCutIndex: 5, compCutIndex: 1},
				}, // PstI: C_TGCA^G rev comp = CTGCAG
			},
			&Frag{
				uniqueID: "backbone",
				fragType: linear,
				Seq:      "",
			},
			&Backbone{
				Strands:  []bool{true},
				Cutsites: []int{20},
				Seq:      "",
				Enzymes:  []string{"PstI"},
			},
			false,
		},
		{
			"digest pSB1A3 with two enzymes: PstI and EcoRI",
			args{
				&Frag{
					Seq: "",
				},
				[]enzyme{
					{name: "PstI", recog: "CTGCAG", seqCutIndex: 5, compCutIndex: 1},
					{name: "EcoRI", recog: "GAATTC", seqCutIndex: 1, compCutIndex: 5},
				}, // rev comp = CTGCAG
			},
			&Frag{
				uniqueID: "backbone",
				fragType: linear,
				Seq:      "",
			},
			&Backbone{
				Strands:  []bool{true, true},
				Cutsites: []int{20, 2134},
				Seq:      "",
				Enzymes:  []string{"PstI", "EcoRI"},
			},
			false,
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			gotDigested, gotBackbone, err := digest(tt.args.frag, tt.args.enz)
			if (err != nil) != tt.wantErr {
				t.Errorf("digest() error = %v, wantErr %v", err, tt.wantErr)
				return
			}

			if !reflect.DeepEqual(gotDigested, tt.wantDigested) {
				diff := deep.Equal(gotDigested, tt.wantDigested)
				t.Errorf("digest.Frag = %v, want %v, diff = %v",
					gotDigested, tt.wantDigested, diff)
			}

			if !reflect.DeepEqual(gotBackbone, tt.wantBackbone) {
				t.Errorf("digest.Backbone = %v, want %v", gotBackbone, tt.wantBackbone)
			}
		})
	}
}

func Test_newEnzyme(t *testing.T) {
	type args struct {
		name  string
		recog string
	}
	tests := []struct {
		name       string
		args       args
		wantEnzyme enzyme
	}{
		{
			"no cut index and no hangover index",
			args{
				"bad",
				"GAATTC",
			},
			enzyme{
				"",
				"",
				-1,
				-1,
			},
		},
		{
			"too many cut indexes",
			args{
				"bad",
				"GA^AT^TC",
			},
			enzyme{
				"",
				"",
				-1,
				-1,
			},
		},
		{
			"too many hangover indexes",
			args{
				"bad",
				"GA_AT_TC",
			},
			enzyme{
				"",
				"",
				-1,
				-1,
			},
		},
		{
			"no hangover",
			args{
				"e1",
				"GA^_ATTC",
			},
			enzyme{
				"e1",
				"GAATTC",
				2,
				2,
			},
		},
		{
			"cut less than hangover",
			args{
				"e1",
				"G^AATT_C",
			},
			enzyme{
				"e1",
				"GAATTC",
				1,
				5,
			},
		},
		{
			"hangover less than CUT",
			args{
				"e1",
				"G_AATT^C",
			},
			enzyme{
				"e1",
				"GAATTC",
				5,
				1,
			},
		},
		{
			"EcoRI",
			args{
				"EcoRI",
				"G^AATT_C",
			},
			enzyme{
				name:         "EcoRI",
				recog:        "GAATTC",
				seqCutIndex:  1,
				compCutIndex: 5,
			},
		},
	}
	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			gotEnzyme := newEnzyme(tt.args.name, tt.args.recog)

			if !reflect.DeepEqual(gotEnzyme, tt.wantEnzyme) {
				diff := deep.Equal(gotEnzyme, tt.wantEnzyme)
				t.Errorf("got = %v, want %v, diff = %v",
					gotEnzyme, tt.wantEnzyme, diff)
			}

		})
	}

}
